INSERT INTO episode (episode_id, episode_name, description, season_id) VALUES (1, 'Episode One', 'It''s the early 90s, the first day of school and a terrible start for 16-year-old Erin as she wakes up to find her cousin Orla reading her diary; Granda Joe announces there''s a bomb on the bridge.', 1);
INSERT INTO episode (episode_id, episode_name, description, season_id) VALUES (2, 'Episode Two', 'Everyone is really excited when Sister Michael announces the school trip to Paris; Back at the Quinn house, Uncle Colm is visiting and the whole family are losing the will to live.', 1);
INSERT INTO episode (episode_id, episode_name, description, season_id) VALUES (3, 'Episode Three', 'The girls are tense about a big exam and naturally jump at a dubious opportunity to get out of it, especially as it involves spending time with the beautiful Father Peter.', 1);
INSERT INTO episode (episode_id, episode_name, description, season_id) VALUES (4, 'Episode Four', 'Erin is so excited that teenage Katya ''a child of Chernobyl'' is coming to stay, but Katya is not exactly grateful to be in Derry; there are developments in Granda Joe''s love life.', 1);
INSERT INTO episode (episode_id, episode_name, description, season_id) VALUES (5, 'Episode Five', 'The Quinn family rush to get out of Derry before the Orange Parades start, but Aunt Sarah''s tarot cards predict a disaster for this trip and, for once, she might be right.', 1);
INSERT INTO episode (episode_id, episode_name, description, season_id) VALUES (6, 'Episode Six', 'Erin is over the moon when she becomes editor of the school magazine; Orla''s obsessed by step aerobics; Da Gerry is in trouble with Ma Mary; There''s romance for Aunt Sarah.', 1);
INSERT INTO episode (episode_id, episode_name, description, season_id) VALUES (7, 'Across the Barricade', 'Erin and co are excited to attend an outdoor pursuits weekend as part of a peace initiative, but peace is the last thing on Michelle''s mind once she finds out that there will be Protestant lads there.', 2);
INSERT INTO episode (episode_id, episode_name, description, season_id) VALUES (8, 'Ms De Br√∫n and the Child of Prague', 'A new English teacher arrives at Our Lady Immaculate, and the girls find themselves inspired.', 2);
INSERT INTO episode (episode_id, episode_name, description, season_id) VALUES (9, 'The Concert', 'When Ma Mary forbids the girls from seeing Take That in Belfast, they will stop at nothing to get there.', 2);
INSERT INTO episode (episode_id, episode_name, description, season_id) VALUES (10, 'The Curse', 'The Quinn family cause uproar at a family wedding, and the consequences of the girls'' bad behaviour are very serious, especially for cousin Eammon.', 2);
INSERT INTO episode (episode_id, episode_name, description, season_id) VALUES (11, 'The Prom', 'Jenny Joyce organises a 50s style prom, but the girls are more intrigued by the arrival of a new girl. Erin, Clare and Michelle are looking for prom dates, but Orla has got someone special in mind.', 2);
INSERT INTO episode (episode_id, episode_name, description, season_id) VALUES (12, 'The President', 'As Christmas approaches, excitement in Derry is at fever pitch for President Bill Clinton''s visit, while James gets a surprise and Granda Joe embarks on a mysterious plan of his own.', 2);
